;function coolCheck(a){if(a.is(":checked")){a.parent(".cool-check").addClass("checked")}else{a.parent(".cool-check").removeClass("checked")}}function coolRadio(a){$(a).each(function(){if($(this).is(":checked")){$(this).parent(".cool-radio").addClass("checked")}else{$(this).parent(".cool-radio").removeClass("checked")}})}function coolFile(b){if(b.val()){var a=b.val().split("\\");b.siblings(".attached").html(a[a.length-1]).addClass("successful")}}function coolForm(){$("form select").each(function(){if(!($(this).parent().hasClass("cool-select"))){$(this).wrap('<div class="cool-select"></div>')}});$('form input[type="checkbox"]').each(function(){if(!($(this).parent().hasClass("cool-check"))){$(this).wrap('<div class="cool-check"></div>');coolCheck($(this))}});$("form").on("change",'input[type="checkbox"]',function(){coolCheck($(this))});$('form input[type="radio"]').each(function(){if(!($(this).parent().hasClass("cool-radio"))){$(this).wrap('<div class="cool-radio"></div>');coolRadio($(this))}});$("form").on("change",'input[type="radio"]',function(){var a=$(this).attr("name");coolRadio('[name="'+a+'"]')});$('form input[type="file"]').each(function(){if(!($(this).parent().hasClass("cool-file"))){$(this).wrap('<div class="cool-file"></div>');$(this).after('<button type="button"></button><span class="attached"></span>');coolFile($(this))}});$("form").on("change",'input[type="file"]',function(){coolFile($(this))})}$('[type="reset"]').on("click",function(){$(this).closest("form").find(".cool-check, .cool-radio").removeClass("checked");$(this).closest("form").find(".attached").empty().removeClass("successful")});$(document).ready(coolForm);$(window).load(coolForm);